import style from "./menu-list.module.css";
import React from "react";

class MenuList extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      isHovered: false,
      type: "secondary",
    };
  }
  handleMouseEnter = () => {
    this.setState({ isHovered: true });
  };
  handleMouseLeave = () => {
    this.setState({ isHovered: false });
  };
  toggleStatus = () => {
    this.state.isHovered ? this.setState({type: "secondary"}) : this.setState({type: "primary"})
  }

  render() {
    const { icon: Component, text, link, type } = this.props;
    const { isHovered } = this.state;

    return (
      <li
        className={style.menuElem}
        onMouseEnter={this.handleMouseEnter}
        onMouseLeave={this.handleMouseLeave}
      >
        <Component type={this.state.type} />
        <button onClick={link} className={"text text_type_main-default"}>
          {text}
        </button>
      </li>
    );
  }
}

export default MenuList;
