import style from "./menu-list.module.css"
import React from 'react';

class MenuList extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            isHovered: false,
            type: "primary",
        }
      }
      handleMouseEnter = () => {
        this.setState({isHovered: true})
      }
      handleMouseLeave = () => {
        this.setState({isHovered: false})
      }

    render() {
        const { icon: Component, text, link} = this.props;
        const { isHovered } = this.state;

        const RenderCompanent = {
            backgroundColor: isHovered ? 'lightblue' : 'white'

          };

        return (
        <li className={style.menuElem}>
            <Component onMouseEnter={this.setState({type: "secondary"})} onMouseLeave={this.state.type="primary"}/>
            <button onClick={link} className={"text text_type_main-default"}>{text}</button>
        </li>
        )
    }
    }

export default MenuList
