import React, {useEffect} from "react";
import { CloseIcon } from "@ya.praktikum/react-developer-burger-ui-components";
import styles from "./template.module.css"

function Template({analysis}) {
    if (analysis[0] !== undefined) {
    const [status, setStatus] = React.useState(false);
    const toggleStatus = () => {
      setStatus(!status);
    };
    const checkStatus = () => {
      return status === false ? styles.disabled : styles.visible;
    };

    useEffect(() => {
        if (analysis[0] !== undefined) {
          toggleStatus();
        }
      }, [analysis]);

     
        const [{calories, carbohydrates, fat ,image_large, name, proteins, type}] = analysis
      
    return (
        <template className={`${styles.templ} ${checkStatus()}`}>
            <div className={`p-10 ${styles.ingrCont}`}>
                <div className={styles.headCont}>
                    <h2 className="text text_type_main-large">Детали ингредиента</h2>
                    <CloseIcon type="primary" onClick={toggleStatus}/>
                </div>
                <section className={styles.section}>
                <img src={image_large} className={styles.img} alt={name} />
                <div>
                    <h3 className="mb-8 text text_type_main-medium">{name}</h3>
                    <ul className={styles.ingrList}> 
                        <li className={styles.ingrInfo}>
                            <p className="text text_type_main-default text_color_inactive">Калории,ккал</p>
                            <span className="text text_type_main-default text_color_inactive">123</span>
                        </li>
                    </ul>
                </div>
                </section>
            </div>
        </template>
    )
}
}
export default Template
