import {
  CurrencyIcon,
  Button,
  LockIcon,
  DeleteIcon,
  DragIcon,
  ConstructorElement,
} from "@ya.praktikum/react-developer-burger-ui-components";
import React, { useEffect } from "react";
import style from "./burger-constructor.module.css";
import GetBun from "./get-bun/get-bun";

function BurgerConstructor({
  ingredients,
  removeIngredient,
  seeAnalysis,
  orderStatus,
}) {
  const totalPrice = ingredients.reduce(
    (acc, ingredient) => acc + ingredient.props.price,
    0
  );
  const handleRemoveIngredient = (index) => {
    removeIngredient(index);
  };
// значение для булки по умолчанию, если результат функции {null}
  let props = {
    "_id":"60666c42cc7b410027a1a9b1",
    "name":"Краторная булка N-200i",
    "type":"bun",
    "proteins":80,
    "fat":24,
    "carbohydrates":53,
    "calories":420,
    "price":30,
    "image":"https://code.s3.yandex.net/react/code/bun-02.png",
    "image_mobile":"https://code.s3.yandex.net/react/code/bun-02-mobile.png",
    "image_large":"https://code.s3.yandex.net/react/code/bun-02-large.png",
    "__v":0
  };

  const bunFiltr = () => {
    const filteredBuns = ingredients.filter(
      (ingr) => ingr.props.type === "bun"
    );
    const lastBun = filteredBuns[filteredBuns.length - 1];
    return lastBun || {props};
  };
  let bun = bunFiltr().props

  const fillinFiltr = () => {
    return ingredients.filter((ingr) => ingr.props.type !== "bun");
  };

  // const borderStyleUp = [true, false];
  // скрываем скроллбар при низкой высоте окна

const scrollBar = document.getElementById(id="scrollBar")

  return (
    <section aria-label="Конструктор" className={`mt-5 ${style.section}`}>
      <ul>
        <li className={`mb-4 ${style.component}`}>
          <div>
            <DragIcon />
          </div>
          <ConstructorElement
            type="top"
            isLocked={true}
            text={bun.name}
            price={bun.price}
            thumbnail={bun.image_mobile}
          />
        </li>
        <ul className={`${style.listComponents} ${style.scrollBar}`} id="scrollBar">
        {fillinFiltr().map((ingredient, index) => (
          <li key={index}
          className={`mb-4 ${style.component}`}>
            <div>
              <DragIcon key={index}/>
            </div>
            <ConstructorElement
            key={index}
            text={ingredient.props.name}
            price={ingredient.props.price}
            thumbnail={ingredient.props.image_mobile}
            handleClose={() => handleRemoveIngredient(index)}
          />
          </li>
        ))}
        </ul>
        <li className={`${style.component}`}>
          <div>
            <DragIcon />
          </div>
        
        <ConstructorElement
          type="bottom"
          isLocked={true}
          text={bun.name}
          price={bun.price}
          thumbnail={bun.image_mobile}
        />
        </li>
        {/* <GetBun
          bun={bunFiltr()}
          seeAnalysis={seeAnalysis}
          bordStyle={borderStyleUp[0]}
        />      
        <ul className="custom-scroll">
          {fillinFiltr().map((ingredient, index) => (
            <li
              key={index}
              className={`mb-4 ${style.component}`}
              onClick={() => seeAnalysis(ingredient.props)}
            >
              <div>
                <DragIcon />
              </div>
              
              <div className={` ${style.cart}`}>
                <img
                  src={ingredient.props.image_mobile}
                  className={style.image}
                  alt={ingredient.props.name}
                />
                <h3 className={`text text_type_main-default ${style.cardName}`}>
                  {ingredient.props.name}
                </h3>
                <div className={`${style.price}`}>
                  <p className={"text text_type_digits-default"}>
                    {ingredient.props.price}
                  </p>
                  <CurrencyIcon />
                </div>
                <div style={{cursor: "pointer"}}>
                  <DeleteIcon onClick={() => handleRemoveIngredient(index)} />
                </div>
              </div>
            </li>
          ))}
        </ul>
        <GetBun
          bun={bunFiltr()}
          seeAnalysis={seeAnalysis}
          bordStyle={borderStyleUp[1]}
        /> */}
      </ul>
      <div className={`mt-8 ${style.price}`}>
        <div className={`${style.price} ${style.price_icon}`}>
          <h3 className="text text_type_digits-medium">{totalPrice}</h3>
          <CurrencyIcon />
        </div>
        <Button
          htmlType="button"
          type="primary"
          size="large"
          onClick={orderStatus}
        >
          Оформить заказ
        </Button>
      </div>
    </section>
  );
}

export default BurgerConstructor;
