import React from "react";
import BurgerIngredients from "./burger-ingredients/burger-ingredients"
import BurgerConstructor from "./burger-constructor/burger-constructor"
import mainStyles from "./main.module.css"

function AppMain({data}) {

  const [setIngredient, addIngridient] = React.useState([])

  const getIngridients = (ingredient) => {
    addIngridient([...setIngredient, ingredient]);
  };
  const removeIngr = (index) => {
    const updatedIngr = [...setIngredient];
    updatedIngr.splice(index, 1)
  }

return(
  <main className={mainStyles.main}>
    <h1 className="text text_type_main-large">Соберите бургер</h1>
    <div className={mainStyles.container}>
        <BurgerIngredients addNewIngridient={getIngridients} data={data}/>
        <BurgerConstructor setIngredients={setIngredient} removeIngredient={removeIngredient}/>
    </div>
    
  </main>
  )
}
export default AppMain
