function BurgerCart(props) {
  console.log(props)
  const { name, image, price, _id } = props;

  const addIngredient = () => {
    props.addIngredient({ name, image, price });
  };

  return (
    <div className={style.cart} key={props._id} onClick={props.addIngredient}>
      <img src={props.image} alt={props.name} />
      <div className={style.cartPrice}>
        <p className="mt-2 text text_type_digits-default">{props.price}</p>
        <CurrencyIcon />
      </div>
      <h3 className={`mt-3 text text_type_main-small ${style.cartName}`}>{props.name}</h3>
    </div>
  );
}
  
  export default BurgerCart;
