import React from "react";
import {Tab} from '@ya.praktikum/react-developer-burger-ui-components'
import BurgerCart from './burger-cart/burger-cart'
import {data} from "../../../utils/data"

class BurgerIngredients extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            current: "One"
        }
    }
    burger = () => data.filter((b) => b.type === "bun");
    sauce = () => data.filter((s) => s.type === "sauce");
    filling = () => data.filter((f) => f.type === "main");

    render () {
        const { current } = this.state;
        return(
                <div>
                    <nav style={{ display: 'flex' }} className="mt-5">
                        <Tab value="one" active={current === 'one'} onClick={setCurrent}>
                            Булки
                        </Tab>
                        <Tab value="two" active={current === 'two'} onClick={setCurrent}>
                            Соусы
                        </Tab>
                        <Tab value="three" active={current === 'three'} onClick={setCurrent}>
                            Начинки
                        </Tab>
                    </nav>
                    <div className="cardContainer">
                        <h2 className="mt-10 text text_type_main-medium">Булки</h2>
                        <BurgerCart/>
                        <h2 className="mt-10 text text_type_main-medium">Соусы</h2>
                        <BurgerCart/>
                        <h2 className="mt-10 text text_type_main-medium">Начинки</h2>
                        <BurgerCart/>
                    </div>
                </div>
            )
    }
}
