import React from "react";
import {Tab} from '@ya.praktikum/react-developer-burger-ui-components'
import BurgerCart from './burger-cart/burger-cart'
import {data} from "../../../utils/data"

class BurgerIngredients extends React.Component {
    constructor(props) {
      super(props);
      this.state = {
        current: "one", // Initialize the current tab
      };
    }
  
    setCurrentTab = (value) => {
      this.setState({ current: value }); // Update the current tab
    };
  
    render() {
      const { current } = this.state;
  
      // Filter data based on the current tab
    
  
      return (
        <div>
          <nav style={{ display: "flex" }} className="mt-5">
            <Tab value="one" active={current === "one"} onClick={() => this.setCurrentTab("one")}>
              Булки
            </Tab>
            <Tab value="two" active={current === "two"} onClick={() => this.setCurrentTab("two")}>
              Соусы
            </Tab>
            <Tab value="three" active={current === "three"} onClick={() => this.setCurrentTab("three")}>
              Начинки
            </Tab>
          </nav>
          <div className="cardContainer">
            <h2 className="mt-10 text text_type_main-medium"> {current === "one" ? "Булки" : current === "two" ? "Соусы" : "Начинки"} </h2>
            <BurgerCart data={filteredData} />
          </div>
        </div>
      );
    }
  }

export default BurgerIngredients
